import { Link } from 'react-router-dom';
import UseVideoList from './hooks/useVideoList';
import classes from './styles/Videos.module.css';
import Video from './Video';
import InfiniteScroll from 'react-infinite-scroll-component'
import { useState } from 'react';
const Videos = () => {
    
    const { Videos, Loading, Error, hasMore } = UseVideoList(0)
    const [page , setPage] = useState(1)
    const vidLinkQuiz = (vid) =>{
       return (
            <Link to='/quiz' key={vid.youtubeID}>
                <Video title={vid.title} id={vid.youtubeID} noq={vid.noq} />
            </Link>
        )
    }
    const vidLinkNoQuiz = (vid) =>{
        return (
             <Link to='/' key={vid.youtubeID}>
                 <Video title={vid.title} id={vid.youtubeID} noq={vid.noq} notfound='No quiz found'/>
             </Link>
         )
     }

    return (
        <div className={classes.videos}>
            {
                Videos.map((videos) => (
                   <InfiniteScroll dataLength={videos.length} hasMore={hasMore} next>
                       { videos.map((sinVid) => (sinVid.noq === 0) ? vidLinkNoQuiz(sinVid) : vidLinkQuiz(sinVid))}
                   </InfiniteScroll>
                ))
            }
            {!Loading && Videos.length === 0 &&
                (<h3>No Data Found!</h3>)
            }
            {
                Error &&
                (<h3>An Error Occured!</h3>)
            }
            {Loading && (<h2>Fetching Data... </h2>)}
        </div>
    );
}

export default Videos;
