import { useState } from 'react';
import InfiniteScroll from 'react-infinite-scroll-component';
import { Link } from 'react-router-dom';
import UseVideoList from './hooks/useVideoList';
import Video from './Video';
const Videos = () => {
    const [page, setPage] = useState(1)
    const { Videos, Loading, Error, hasMore } = UseVideoList(page)

    const vidLinkQuiz = (vid) => {
        return (
            <Link to='/quiz' key={vid.youtubeID}>
                <Video title={vid.title} id={vid.youtubeID} noq={vid.noq} />
            </Link>
        )
    }
    const vidLinkNoQuiz = (vid) => {
        return (
            <Link to='/' key={vid.youtubeID}>
                <Video title={vid.title} id={vid.youtubeID} noq={vid.noq} notfound='No quiz found' />
            </Link>
        )
    }

    return (
        <div>
            {
                <InfiniteScroll dataLength={Videos.length} hasMore={hasMore} next={() => setPage(page + 8)} loader={<h1>Getting Videos...</h1>} endMessage={p}> 
                    {
                        Videos.map((videos) => (
                            videos.map((sinVid) => (sinVid.noq === 0)
                                ?
                                vidLinkNoQuiz(sinVid)
                                :
                                vidLinkQuiz(sinVid))
                        ))

                    }
                    {console.log(Videos)}
                </InfiniteScroll>
            }
            {!Loading && Videos.length === 0 &&
                (<h3>No Data Found!</h3>)
            }
            {
                Error &&
                (<h3>An Error Occured!</h3>)
            }
            {Loading && (<h2>Fetching Data... </h2>)}
        </div>
    );
}

export default Videos;
