import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Button from './Button';
import Checkbox from './Checkbox';
import classes from './styles/Form.module.css';
import TextInput from './TextInput';
const Form = ({ Name, ...rest }) => {
    const formClass = classes.form
    const classN = Name

   
    return (
        <form className={`${classN} ${formClass}`} {...rest} onSubmit={handleSubmit}>
            <TextInput required type='text' icon='person' placeholder='Enter Name' value={username} onChange={(e) => { setUsername(e.target.value) }} />
            <TextInput required icon="alternate_email" placeholder="Enter Email" type="email" value={email} onChange={(e) => { setEmail(e.target.value) }} />
            <TextInput required icon="lock" placeholder="Enter Password" type="password" value={password} onChange={(e) => { setPassword(e.target.value) }} />
            <TextInput required icon="lock_clock" placeholder="Confirm Password" type="password" value={confirmPassword} onChange={(e) => { setConfirmPassword(e.target.value) }} />
            <br />
            <Checkbox required text="I agree to the Terms &amp; Conditions" value={agree} onChange={(e) => { setAgree(e.target.value) }} />
            <br />
            <Button disabled={loading} type='submit'><span>Submit now</span></Button>

            {error && <p className='error'>{error}</p>}
            <br />
            <div className={classes.info}>
                Already have an account? <Link to="/login">Login</Link> instead.
            </div>

        </form>
    );
}

export default Form;
