import Button from './Button';
import classes from './styles/Form.module.css';
import TextInput from './TextInput';
import { useAuth } from '../context/AuthContext';
const LoginForm = ({ Name, ...rest }) => {
    const formClass = classes.form
    const classN = Name

    const { login } = useAuth()

    const [email, setEmail] = useState('');;
    const [password, setPassword] = useState('');
    const [loading, setLaoding] = useState();
    const [Error, setError] = useState('');


    async function handleSubmit(e) {
        e.preventDefault();

        if (password !== confirmPassword) {
            return setError(`password doesn't match`)
        }
        if(agree !== true){
            return setError(`Please agreed to all terms and condtions`) 
        }

        try {
            setError('')
            setLaoding(false)
            await login(email, password, username)

        } catch (err) {
            console.log(err.message)
            setLaoding(true)
            setError(err)
        }
    }

    return (
        <form className={`${classN} ${formClass}`} {...rest} onSubmit={handleSubmit}>
            <TextInput required type='email' value={email} onChange={(e) => { setEmail(e.target.value) }}  icon='alternate_email' placeholder='Enter Name' />
            <TextInput required value={password} icon="lock" onChange={(e) => { setPassword(e.target.value) }} placeholder="Enter Password" type="password" />
            {Error && <p className='error'>{Error}</p>}
            <br/>
            <Button type='submit' disabled={loading}><span>Submit now</span></Button>
            <div className={classes.info}>Don't have an account? <a href="/signup">Signup</a> instead.</div>
        </form>
    );
}

export default LoginForm;
