import { useState } from 'react';
import { Link } from 'react-router-dom';
import Button from './Button';
import Checkbox from './Checkbox';
import classes from './styles/Form.module.css';
import TextInput from './TextInput';
import {useAuth} from '../context/AuthContext'

const Form = ({ ...rest }) => {
    const {signup} = useAuth()

    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');;
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [agree, setAgree] = useState()
    const [loading, setLaoding] = useState();
    const [Error, setError] = useState('');

    
    async function handleSubmit(e){
        e.preventDefault();

        if(password !== confirmPassword){
            return setError(`password doesn't match`)
        }
       
        try{
            setError('')
            setLaoding(false)
        await signup(email, password, username)

        }catch(err){
            console.log(err.message)
            setLaoding(true)
            setError(`Failed to create account`)
        }
    }

    return (
        <form className={classes.form} style={{ height: '500px' }} {...rest} onSubmit={handleSubmit}>
            <TextInput type='text' icon='person' placeholder='Enter Name' value={username} onChange={(e) => { setUsername(e.target.value) }} />
            <TextInput icon="alternate_email" placeholder="Enter Email" type="email" value={email} onChange={(e) => { setEmail(e.target.value) }} />
            <TextInput icon="lock" placeholder="Enter Password" type="password" value={password} onChange={(e) => { setPassword(e.target.value) }} />
            <TextInput icon="lock_clock" placeholder="Confirm Password" type="password" value={confirmPassword} onChange={(e) => { setConfirmPassword(e.target.value) }} />
            <br />
            <Checkbox text="I agree to the Terms &amp; Conditions" value={agree} onChange={(e) => { setAgree(e.target.checked) }} />
            <br />
            {Error && <p className='error'>{Error}</p>}
            <Button dis type='submit'><span>Submit now</span></Button>
            <br />
            <div className={classes.info}>
                Already have an account? <Link to="/login">Login</Link> instead.
            </div>

        </form>
    );
}

export default Form;
