import { Link } from 'react-router-dom';
import UseVideoList from './hooks/useVideoList';
import classes from './styles/Videos.module.css';
import Video from './Video';
const Videos = () => {
    
    const { Videos, Loading, Error } = UseVideoList()

    const vidLinkQuiz = (vid) =>{
       return (
            <Link to='/quiz' key={vid.youtubeID}>
                <Video title={vid.title} id={vid.youtubeID} noq={vid.noq} />
            </Link>
        )
    }
    const vidLinkNoQuiz = (vid) =>{
        return (
             <Link to='/' key={vid.youtubeID}>
                 <Video title={vid.title} id={vid.youtubeID} noq={vid.noq} />
             </Link>
         )
     }

    return (
        <div className={classes.videos}>
            {
                Videos.map((videos) => (
                    videos.map((sinVid) => sinVid.noq === 0 && vidLinkNoQuiz(sinVid) || vid)
                ))
            }
            {!Loading && Videos.length === 0 &&
                (<h3>No Data Found!</h3>)
            }
            {
                Error &&
                (<h3>An Error Occured!</h3>)
            }
            {Loading && (<h2>Fetching Data... </h2>)}
        </div>
    );
}

export default Videos;
