    return (
        <>
            {questions.map((sinQuestions, index) => (
                <div key={Math.random()}>
                    <h1>{sinQuestions.title}</h1>
                    <h4>Question can have multiple answers</h4>
                    <Answers index={index} question={sinQuestions} choice={sinQuestions.options} />
