import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Button from './Button';
import classes from './styles/Form.module.css';
import TextInput from './TextInput';
const LoginForm = ({ Name, ...rest }) => {
    const formClass = classes.form
    const classN = Name

    const navigate = useNavigate()
    const { login } = useAuth()
    const [email, setEmail] = useState('');;
    const [password, setPassword] = useState('');
    const [loading, setLaoding] = useState();
    const [Error, setError] = useState('');


    async function handleSubmit(e) {
        e.preventDefault();
        try {
            setError('')
            setLaoding(false)
            await login(email, password);
            navigate('/', { replace: true })
            setEmail('')
            setPassword('')
        } catch (err) {
            console.log(err.message)
            setLaoding(true)
            setError(err)
        }
    }

    return (
        <form className={`${classN} ${formClass}`} {...rest} onSubmit={handleSubmit}>
            <TextInput required type='email' value={email} onChange={(e) => { setEmail(e.target.value) }} icon='alternate_email' placeholder='Enter Email' />
            <TextInput required value={password} icon="lock" onChange={(e) => { setPassword(e.target.value) }} placeholder="Enter Password" type="password" />
            {Error && <p className='error'>{Error}</p>}
            <br />
            <Button type='submit' disabled={loading}><span>Submit now</span></Button>
            <br />
            <div className={classes.info}>Don't have an account? <L href="/signup">Signup</L> instead.</div>
        </form>
    );
}

export default LoginForm;
