import { Link } from 'react-router-dom';
import Button from './Button';
import Checkbox from './Checkbox';
import classes from './styles/Form.module.css';
import TextInput from './TextInput';
import { useState } from 'react';
const Form = ({ ...rest }) => {

    const [username, setUsername] = useState();
    return (
        <form className={classes.form} style={{ height: '500px' }} {...rest}>
            <TextInput type='text' icon='person' placeholder='Enter Name' value={username} onChange={(e) =>{setUsername(e.target.value)}} />
            <TextInput icon="alternate_email" placeholder="Enter Email" type="email" />
            <TextInput icon="lock" placeholder="Enter Password" type="password" />
            <TextInput icon="lock_clock" placeholder="Confirm Password" type="password" />
            <br />
            <Checkbox text="I agree to the Terms &amp; Conditions" />
            <br />
            <Button><span>Submit now</span></Button>
            <br />
            <div className={classes.info}>
                Already have an account? <Link to="/login">Login</Link> instead.
            </div>

        </form>
    );
}

export default Form;
