import { Link } from 'react-router-dom';
import UseVideoList from './hooks/useVideoList';
import classes from './styles/Videos.module.css';
import Video from './Video';
const Videos = () => {
    const { Videos, Loading, Error } = UseVideoList()
    const vidLinkQuiz = (sing) =>{
       return (
            <Link to='/quiz' key={sing.youtubeID}>
                <Video title={sing.title} id={sing.youtubeID} noq={sing.noq} />
            </Link>
        )
    }
    const vidLinkNoQuiz = (sing) =>{
        return (
             <Link to='/quiz' key={sing.youtubeID}>
                 <Video title={sing.title} id={sing.youtubeID} noq={sing.noq} />
             </Link>
         )
     }
    return (
        <div className={classes.videos}>
            {
                Videos.map((videos) => (
                    videos.map((val) => (vidLinkQuiz(val)))
                ))
            }
            {!Loading && Videos.length === 0 &&
                (<h3>No Data Found!</h3>)
            }
            {
                Error &&
                (<h3>An Error Occured!</h3>)
            }
            {Loading && (<h2>Fetching Data... </h2>)}
        </div>
    );
}

export default Videos;
