import Button from './Button';
import classes from './styles/Form.module.css';
import TextInput from './TextInput';
const LoginForm = ({ Name, ...rest }) => {
    const formClass = classes.form
    const classN = Name
    return (
        <form className={`${classN} ${formClass}`} {...rest}>
            <TextInput type='email' icon='alternate_email' placeholder='Enter Name' />
            <TextInput icon="lock" placeholder="Enter Password" type="password" />
            <Button><span>Submit now</span></Button>
            <div className={classes.info}>Don't have an account? <a href="/signup">Signup</a> instead.</div>
        </form>
    );
}

export default LoginForm;
